import * as React from 'react';
import { Card, message } from 'antd';
import { BizForm } from 'antd-more';
import waitTime from './utils/waitTime';

const { StepsForm, ItemInput, ItemCaptcha, ItemPassword } = BizForm;

const Demo: React.FC = () => {
  const [form1] = BizForm.useForm();
  const [form2] = BizForm.useForm();

  return (
    <Card
      title="忘记密码"
      extra={<a>返回登录</a>}
      style={{ width: '100%', maxWidth: 680, margin: '0 auto' }}
    >
      <StepsForm
        onFinish={async (values) => {
          console.log(values);
          // 整个表单提交时
          await waitTime();
          message.success('设置新密码成功');
        }}
        stepsRender={(_, stepsDom) => <div style={{ width: '80%', margin: '0 auto' }}>{stepsDom}</div>}
      >
        <StepsForm.StepForm
          title="验证手机号"
          size="large"
          submitter={{
            nextButtonProps: {
              block: true
            }
          }}
          style={{ width: '75%', margin: '0 auto' }}
          requiredMark={false}
          form={form1}
          onFinish={async (values) => {
            console.log(values);
            // 验证验证码
            await waitTime();
          }}
        >
          <ItemInput
            name="mobile"
            type="mobile"
            inputProps={{
              placeholder: '请输入11位手机号'
            }}
            label="手机号"
            required
          />
          <ItemInput
            name="code"
            validateTrigger="onBlur"
            label="图形验证码"
            after={
              <img src="data:image/jpg;base64," alt="图形验证码" />
            }
            required
          />
          <ItemCaptcha
            name="smsCode"
            validateTrigger="onBlur"
            label="验证码"
            normalize={(val) => val.replace(/[^\d]/g, '')}
            inputProps={{ maxLength: 6 }}
            onGetCaptcha={async () => {
              try {
                await form1.validateFields(['mobile', 'code']);
              } catch (err) {
                message.error('请输入正确的手机号和图形验证码');
                return false;
              }

              // 发送验证码
              // const mobile = form1.getFieldValue('mobile');
              // const code = form1.getFieldValue('code');
              // return sendSms({ mobile, code }).then(() => {
              //   message.success(`短信验证码已发送至${mobile}`);
              // });

              await waitTime();
            }}
            required
          />
        </StepsForm.StepForm>
        <StepsForm.StepForm
          title="设置新密码"
          size="large"
          submitter={{
            noPrev: true,
            submitText: "确认",
            submitButtonProps: {
              block: true
            }
          }}
          style={{ width: '75%', margin: '0 auto' }}
          requiredMark={false}
          form={form2}
        >
          <ItemPassword
            name="password"
            min={6}
            max={16}
            inputProps={{
              placeholder: '6-16位密码，区分大小写'
            }}
            label="新密码"
            validateTrigger="onBlur"
            required
          />
          <ItemPassword
            name="oldPassword"
            inputProps={{
              placeholder: '再次输入新密码'
            }}
            dependencies={['password']}
            validateTrigger="onBlur"
            label="确定密码"
            rules={[
              {
                validator(rules, value) {
                  let errMsg = '';
                  if (!value) {
                    errMsg = '请再次输入新密码';
                  } else if (value !== form2.getFieldValue('password')) {
                    errMsg = '两次输入的密码不一致';
                  }
                  if (errMsg) {
                    return Promise.reject(errMsg);
                  }
                  return Promise.resolve();
                }
              }
            ]}
          />
        </StepsForm.StepForm>
      </StepsForm>
    </Card>
  );
}

export default Demo;